import 'dart:convert';
import 'package:trabalho_consulta_api/model/filme.dart';

class FilmeApi {
  String respostaApi = '''
    [
      {
          "id": 1,
          "titulo": "Homem de Ferro 2",
          "resumo": "Filme do Homem de Ferro",
          "atores": "Robert Downey",
          "capa": "data:image/jpeg;base64,",
          "duracao": 140
      },
      {
          "id": 2,
          "titulo": "Vingadores: Ultimato",
          "resumo": "Filme dos Vingadores",
          "atores": "Chris Evans, Robert Downey Jr.",
          "capa": "https://play-lh.googleusercontent.com/nz5s7-HJ3iijnvitzCwsa_oD5l7saKAnF_Hv3qGtsP6dtsXTqESZmfuQKqyZZD4UywyB",
          "duracao": 182
      },
      {
          "id": 3,
          "titulo": "Adão Negro",
          "resumo": "Filme do Adão Negro",
          "atores": "Dwayne Johnson,Aldis Hodge,Pierce Brosnan,Sarah Shahi,Ísis Quintessa Swindell",
          "capa": "data:image/jpeg;base64,",
          "duracao": 124
      },
      {
          "id": 4,
          "titulo": "Coringa",
          "resumo": "Filme do Coringa",
          "atores": "Joaquin Phoenix",
          "capa": "https://musicart.xboxlive.com/7/7f365100-0000-0000-0000-000000000002/504/image.jpg?w=1920&h=1080",
          "duracao": 122
      },
      {
          "id": 5,
          "titulo": "Jurassic Park",
          "resumo": "Filme do Jurassic Park",
          "atores": "Sam Neill, Laura Dern",
          "capa": "data:image/jpeg;base64,",
          "duracao": 127
      }
    ]
    ''';

  Future<List<Filme>> getFilmesOffline() async {
    final json = jsonDecode(respostaApi);
    final mapsFilme = json.cast<Map<String, dynamic>>();
    List<Filme> listFilmes = [];

    for (var map in mapsFilme) {
      Filme filme = Filme.fromJson(map);
      listFilmes.add(filme);
    }

    await Future.delayed(const Duration(seconds: 1));

    return listFilmes;
  }
}
